public class WeekTwo {

    public static void main(String[] args) {
        String genom = " ";
        test();
        int a = genom.length();
        System.out.println(a);
    }

    public static int findStopCodon(String dnaStr, int startIndex, String stopCodon) {
        int currIndex = dnaStr.indexOf(stopCodon, startIndex + 3);
        while (currIndex != -1) {
            int diff = currIndex - startIndex;
            if (diff % 3 == 0) {
                return currIndex;
            } else {
                currIndex = dnaStr.indexOf(stopCodon, currIndex + 1);
            }

        }
        return -1;
    }

    public static String findGene(String dna, int where) {
        int startIndex = dna.indexOf("ATG", where); // 0
        if (startIndex == -1) {
            return "";
        }

        int taaIndex = findStopCodon(dna, startIndex, "TAA"); // 3
        int tagIndex = findStopCodon(dna, startIndex, "TAG"); // 2
        int tgaIndex = findStopCodon(dna, startIndex, "TGA"); // 4

        int minIndex = 0;
        //                     tga= 4 (true)     tga(4) < taa (3)(flase)
        if (taaIndex == -1 || (tgaIndex != -1 && tgaIndex < taaIndex)) {
            minIndex = tgaIndex;
        } else {
            minIndex = taaIndex;

        }
        //                     tga= 4 (true)     tga(4) < taa (3)(flase)
        if (minIndex == -1 || (tagIndex != -1 && tagIndex < minIndex)) {
            minIndex = tagIndex;
        }

        if (minIndex == -1) {
            return "";
        }

        return dna.substring(startIndex, minIndex + 3);
    }

    public static void printAllGenes(String dna) {
        int startIndex = 0;
        int counter = 0 ;
        while (true) {
            String currentGene = findGene(dna, startIndex);
            if (currentGene.isEmpty()) {
                break;
            }
            System.out.println(currentGene);
            if (currentGene.length()>60){
            
            }
            startIndex = dna.indexOf(currentGene, startIndex) + currentGene.length();
            counter ++;
        }
        System.out.println(counter);
    }

    public static void testOn(String dna) {
        System.out.println("Testing print all Genes on \n" + dna);
        printAllGenes(dna);
    }

    public static void test() {
        testOn("");

    }

}

